template <>
inline bool pandora::PandoraInputType<april_content::SemiDigitalThresholdList>::IsValid(const april_content::SemiDigitalThresholdList &list) const
{
	if(list.empty())
		return false;

	for(unsigned int i=0 ; i<list.size() ; i++)
	{
		if( !(std::isnan(static_cast<unsigned int>(list.at(i))) || std::isinf(static_cast<unsigned int>(list.at(i)))) )
			return false;
	}

	return true;
}

//------------------------------------------------------------------------------------------------------------------------------------------

template <>
inline bool pandora::PandoraInputType<april_content::SemiDigitalThreshold>::IsValid(const april_content::SemiDigitalThreshold &t) const
{
	return !(std::isnan(static_cast<unsigned int>(t)) || std::isinf(static_cast<unsigned int>(t)));
}

