  float APRILCluster::GetMergedHadronicEnergy()
  {
	  float hadronicEnergy = 0.;

	  hadronicEnergy += GetHadronicEnergy();

	  for(auto& cluster : m_clustersToMerge)
	  {
		  hadronicEnergy += cluster->GetMergedHadronicEnergy();
	  }

	  return hadronicEnergy;
  }

